mapper.update:
UPDATE transport_inventory
    SET frozenQuantity = frozenQuantity + #{frozenIncrement} ,
        availableQuantity = availableQuantity - #{frozenIncrement}
WHERE
    frozenQuantity + #{frozenIncrement} >= 0
    and availableQuantity - #{frozenIncrement} >= 0
    and storageCode = #{inventory.storageCode}
    and itemBatch = #{inventory.itemBatch}
    and quality = #{inventory.quality}

UPDATE transport_inventory
    SET availableQuantity = availableQuantity + #{increment}
WHERE
    and availableQuantity + #{increment} >= 0
    and storageCode = #{inventory.storageCode}
    and itemBatch = #{inventory.itemBatch}
    and quality = #{inventory.quality}
